PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS task_table(task_id TEXT, title, description, init_date, termination_date);
CREATE TABLE IF NOT EXISTS images_table(image_id TEXT PRIMARY KEY, directory TEXT, task_id INTEGER,FOREIGN KEY (task_id) REFERENCES task_table(task_id));
CREATE TABLE IF NOT EXISTS files_table(file_id TEXT PRIMARY KEY, directory TEXT, task_id INTEGER,FOREIGN KEY (task_id) REFERENCES task_table(task_id));
COMMIT;
